import React from 'react'

import styles from './Burger.module.css'
import BurgerIngredient from './BurgerIngredient/BurgerIngredient'

const burger = (props) => {
    console.log("props"+props.ingredient)
    let transformedIngred = Object.keys(props.ingredient).map(igkey => {
             //   console.log("igkey 1"+ igkey);
        return [...Array(props.ingredient[igkey])].map((_, index) => {
           // console.log("igkey 2"+ igkey);
            return <BurgerIngredient key={igkey + index} type={igkey} />
        });
    }).reduce((arr,el)=>{
        return arr.concat(el);
    },[]);
    if(transformedIngred.length===0){
        transformedIngred=<p>Please start add ingredients</p>
    }
    return (
        <div className={styles.Burger}>
            <BurgerIngredient type="bread-top"/>
            {transformedIngred}
            <BurgerIngredient type="bread-bottom"/>
        </div>
    );

}

export default burger;
