import React, { Component } from 'react'
import Auxillary from '../../hoc/Auxillary'
import Burger from '../../components/Burger/Burger'
import BuildControls from '../../components/Burger/BuildControls/BuildControls'

const INGERDIENT_PRICES ={
    salad: 0.4,
    bacon:0.6,
    cheese:0.5,
    meat : 1.2
    
}
class BurgerBuilder extends Component{

    state = {
        ingredient : {
            salad:0,
            bacon:0,
            cheese:0,
            meat:0
        },
        totalPrice:4

    } 

    addIngredientHandler = (type) =>{
        console.log("Here clicked"+type);

        const oldCount=this.state.ingredient[type];
        const updatedCount=oldCount+1;
        const updatedIngredient = {
            ...this.state.ingredient
        };
        updatedIngredient[type]=updatedCount;
        const priceAddition=INGERDIENT_PRICES[type]
        const oldPrice=this.state.totalPrice
        const newPrice=priceAddition+oldPrice
        this.setState({totalPrice :newPrice},{ingredient :updatedIngredient})
    }

    render(){
        return(
            <Auxillary>
                <Burger ingredient={this.state.ingredient}/>
                <BuildControls ingredientAdd={this.addIngredientHandler}/>
            </Auxillary>
        );

    }

}

export default BurgerBuilder;
